function parseHash() {
    // Alternate args should be names and values
    if (location.hash.substring(0, 3) != '#!/') {
        return {};
    }
    var arg_arr = location.hash.substring(3).split('/');
    var args = {};
    for (var i = 0; i < arg_arr.length + 1; i = i + 2) {
        var n = arg_arr[i];
        var v = arg_arr[i + 1];
        if (n && v) {
            args[n] = v;
        }
    }
    return args;
}

